`,(0,e.jsxDEV)(a.pre,{children:(0,e.jsxDEV)(a.code,{className:"language-jsx",children:`// Next.js App Router example
"use client";

import {NextUIProvider, SupportedCalendars} from "@nextui-org/react";
import {CalendarDate, GregorianCalendar} from "@internationalized/date";

export interface ProvidersProps {
  children: React.ReactNode;
}

function createCalendar(identifier: SupportedCalendars) {
  switch (identifier) {
    case "gregory":
      return new GregorianCalendar();
    default:
      throw new Error(\`Unsupported calendar \${identifier}\`);
  }
}

export function Providers({children}: ProvidersProps) {
  const router = useRouter();

  return (
    <NextUIProvider
      locale="hi-IN-u-ca-indian"
      defaultDates={{
        minDate: new CalendarDate(1900, 1, 1),
        maxDate: new CalendarDate(2099, 12, 31),
      }}
      createCalendar={createCalendar}
    >
      {children}
    </NextUIProvider>
  );
}
`},void 0,!1,{fileName:"/Users/russuiulia/new-iasig/docs/content/blog/_mdx_bundler_entry_point-c31ca7b5-2a0d-4ec9-b0a7-af06b0608c7d.mdx",lineNumber:257,columnNumber:1},this)},void 0,!1,{fileName:"/Users/russuiulia/new-iasig/docs/content/blog/_mdx_bundler_entry_point-c31ca7b5-2a0d-4ec9-b0a7-af06b0608c7d.mdx",lineNumber:257,columnNumber:1},this),`
